mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle},
	BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET COUNT = COUNT +1
WHERE BOARD_NO = #{boardNo}

		UPDATE BOARD
        SET STATUS = 'Y'
        WHERE BOARD_NO = #{boardNo}

		UPDATE REPLY
